<script type="text/javascript">
jQuery(document).ready(function( $ ){
  	$(function(){
		var stickyHeader = 100;
		
		$(window).scroll(function() {
			var scroll = parseInt(getCurrentScroll());
          
          	//header on scroll
			if ($('#revamp-header').size() > 0){
				if (scroll > stickyHeader) {
					$('header#revamp-header').addClass('white-bg');
				}else{
                  	$('header#revamp-header').removeClass('white-bg');
                }
			}

		});

		function getCurrentScroll() {
			return window.pageYOffset || document.documentElement.scrollTop;
		}
    });
  
  	$(window).load(function(){
      	var topOffset = document.documentElement.scrollTop;
      	if(topOffset > 100){
          	$('header#revamp-header').addClass('white-bg');
        }
    });
  
  	$('#mobile-menu-button').click(function(){
      	if(!$(this).hasClass('active')){
          	$('#navigation').slideDown();
          	$(this).addClass('active');
        }else{
          	$('#navigation').slideUp();
          	$(this).removeClass('active');
        }
    });
  	
  	if($(window).width() < 992){
      	$('#navigation li.hasSubMenu > a').click(function(e){
          	e.preventDefault();
          	var thisSub = $(this).parent().find('ul.sub-menu');
          	if(!thisSub.hasClass('open')){
              	thisSub.slideDown();
              	thisSub.addClass('open');
            }else{
              	thisSub.slideUp();
              	thisSub.removeClass('open');
            }
        });
    }
  	/*$(window).load(function(){
      	setTimeout(function(){ 
          $('#first-time-visit').modal();
        }, 3000);
    });*/
  	
});

</script>
<!-- end Simple Custom CSS and JS -->
